<script lang="ts">
import { Vue, Component, Mixins, Watch } from 'vue-property-decorator'
import { Action, State } from 'vuex-class'
import BN from 'bignumber.js'
import NumberMixin from '@/mixins/numberMixin'
import cloneDeep from 'lodash/cloneDeep'

import web3Util, { Provider } from '@/utils/web3-util'
import gameUtil, { Cell, GameMap, CellStatus } from '@/utils/game-util'

interface Account {
  user: User;
  liquidity: Liquidity;
}

interface User {
  address: string;
  lastBlock: number;
  reward: string;
}

interface Pair {
  token0: string;
  token1: string;
  percent: string;
}

interface Liquidity {
  XE: Pair;
  XV: Pair;
  VE: Pair;
}

enum Pairs {
  XE = 'XE',
  XV = 'XV',
  VE = 'VE'
}

@Component
export default class Game extends Mixins(NumberMixin) {
  Pairs = Pairs
  CellStatus = CellStatus

  XOR_TOKEN_ADDRESS = '0x40FD72257597aA14C7231A7B1aaa29Fce868F677'
  VAL_TOKEN_ADDRESS = '0xe88f8313e61a97cec1871ee37fbbe2a8bf3ed1e4'

  showArticle = true;
  isMetamaskConnecting = false;
  isWalletConnecting = false;

  async created () {
    await this.getGameInfo()
    await this.getTotalLiquidity()
    await this.generateMap()

    const article = localStorage.getItem('showArticle')
    this.showArticle = article ? JSON.parse(article) : true
    this.fixAnimationIssues()

    if (!this.address.length) return

    try {
      await this.onGetAccountInfo(this.address)
    } catch (error) {
      console.error(error)
    }
  }

  async mounted () {
    web3Util.watchEthereum({
      onAccountChange: (addressList: string[]) => {
        if (addressList.length) {
          this.switchAccount({ address: addressList[0] })
        } else {
          this.disconnectWallet()
        }
      }
    })
  }

  @State(state => state.web3.address) address!: string
  @State(state => state.app.account) account!: Account
  @State(state => state.app.game) game
  @State(state => state.app.map) map!: GameMap

  @Action getAccountInfo!: (address) => Promise<void>
  @Action connectWallet!: (options) => Promise<void>
  @Action switchAccount!: (address) => Promise<void>
  @Action disconnectWallet!: () => Promise<void>
  @Action getGameInfo!: () => Promise<void>
  @Action getTotalLiquidity!: () => Promise<void>
  @Action generateMap!: () => Promise<void>

  @Watch('account')
  async onAccountChanged (account: Account) {
    await this.generateMap()
  }

  get userAddress (): string {
    if (this.address.length) {
      return `${this.address.slice(0, 8)}...${this.address.slice(-6)}`
    }
    return ''
  }

  get lastUpdateDate (): string {
    const isoDate = new Date(this.game.lastUpdateTimestamp || new Date().getTime()).toISOString()
    return `${isoDate.substr(11, 8)} ${isoDate.substr(0, 10)} UTC`
  }

  get rewardAmount (): string {
    return this.$formatNumber(this.account.user.reward, 3)
  }

  async onMetamaskConnect () {
    if (this.isMetamaskConnecting) return
    this.isMetamaskConnecting = true
    try {
      await this.connectWallet({ provider: Provider.Metamask })
    } catch (error) {
      console.error(error)
    }
    this.isMetamaskConnecting = false
  }

  async onWalletConnect () {
    if (this.isWalletConnecting) return
    this.isWalletConnecting = true
    try {
      await this.connectWallet({ provider: Provider.WalletConnect })
    } catch (error) {
      console.error(error)
    }
    this.isWalletConnecting = false
  }

  async onGetAccountInfo (address) {
    await this.getAccountInfo({ address })
  }

  getCellImgPath (cell: Cell): string {
    return gameUtil.getCellImgPath(cell)
  }

  getBarPercent (pair: string): string {
    const { percent } = this.liquidityProportion(pair)
    return new BN(percent).div(100).multipliedBy(85).plus(15).toString()
  }

  liquidityProportion (pair: string): { token0: string; token1: string; percent: string} {
    const format = ({ token0, token1, percent }) => new BN(token1).eq(0)
      ? { token0: '0', token1: '0', percent: '0' }
      : {
        token0: this.$formatNumber(token0, 3),
        token1: this.$formatNumber(token1, 3),
        percent: this.$formatNumber(percent, 4)
      }
    return format({
      token0: new BN(this.account.liquidity[pair].token0),
      token1: new BN(this.account.liquidity[pair].token1),
      percent: new BN(this.account.liquidity[pair].percent)
        .multipliedBy(100)
    })
  }

  closeArticle () {
    this.showArticle = false
    localStorage.setItem('showArticle', String(false))
  }

  fixAnimationIssues () {
    const scripts = [
      'js/slides.min.js',
      'js/fancybox-master/dist/jquery.fancybox.min.js',
      'js/custom.js'
    ].forEach(src => {
      const s = document.createElement('script')
      s.type = 'text/javascript'
      s.src = src
      document.body.appendChild(s)
    })
  }
}
</script>

